// SPDX-License-Identifier: MIT

pragma solidity ^0.8;

contract Task {
    uint256 private count;

    enum TaskStatus {
        CREATED,
        ALLOCATED_BY_FINANCIAL_MANAGER,

        APPROVED_BY_BUDGET_AND_PROCUREMENT_MANAGER,
        APPROVED_BY_PROJECT_MANAGER,
        APPROVED_BY_EXTERNAL_AUDITOR,

        REJECTED_BY_BUDGET_AND_PROCUREMENT_MANAGER,
        REJECTED_BY_PROJECT_MANAGER,
        REJECTED_BY_EXTERNAL_AUDITOR
    }

    struct TaskInfo {
        uint256 id;
        string name;
        string description;
        uint256 projectId;
        uint256 subProjectId;
        uint256 estimatedDuration;
        TaskStatus status;
        uint256 allocatedBudget;
        string remark;
        uint256 createdAt;
    }

    mapping(uint256 => TaskInfo) private tasks;

    event AddedTask(
        uint256 indexed id,
        string indexed name,
        string description,
        uint256 indexed projectId,
        uint256 subProjectId,
        uint256 estimatedDuration,
        TaskStatus status,
        uint256 allocatedBudget,
        uint256 createdAt
    );

    event ChangeedTaskStatus(
        uint256 indexed taskid,
        TaskStatus from,
        TaskStatus to,
        uint256 indexed date,
        address indexed changedBy
    );

    modifier statusIs(TaskInfo memory status, uint256 id) {
        require(tasks[id].status == TaskStatus.CREATED);
        _;
    }

    /*
    Function to add new task to the BC providing all useful info.
    */
    function addTask(
        string memory name,
        string memory description,
        uint256 projectId,
        uint256 subProjectId,
        uint256 estimatedDuration,
        uint256 allocatedBudget
    ) public {
        count++;
        tasks[count] = TaskInfo(
            count,
            name,
            description,
            projectId,
            subProjectId,
            estimatedDuration,
            TaskStatus.CREATED,
            0,
            '',
            block.timestamp
        );

        emit AddedTask(
            count,
            name,
            description,
            projectId,
            subProjectId,
            estimatedDuration,
            TaskStatus.CREATED,
            allocatedBudget,
            block.timestamp
        );
    }

